import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ReservationService {
  private apiUrl = 'http://localhost:3550';
  constructor(private http: HttpClient) { }

  availablility(checkinDate: string, checkoutDate: string) {
    const formData = new FormData();
    formData.append('checkinDate', checkinDate);
    formData.append('checkoutDate', checkoutDate);


    console.log('service---',checkoutDate,checkinDate);

    console.log('service---',typeof(checkoutDate) ,typeof(checkinDate));


    return this.http.post<any>(`${this.apiUrl}/reserve/table`, formData);
  }

  sendData(checkinDate: Date, checkoutDate: Date) {
    // Define the URL of your server endpoint
    const serverUrl = `${this.apiUrl}/reserve/table`; // Replace with your actual server URL

    // Create an object with the data to be sent
    const data = {
      checkinDate,
      checkoutDate
    };
console.log('s',data);

    // Send a POST request to the server with the data
    return this.http.post(serverUrl, data);
  }
}
