import { Component } from '@angular/core';

@Component({
  selector: 'app-table-reservation',
  templateUrl: './table-reservation.component.html',
  styleUrls: ['./table-reservation.component.css']
})
export class TableReservationComponent {
  setInitialDates() {
    const dateCheckin = document.getElementById("date-checkin") as HTMLInputElement;
    const dateCheckout = document.getElementById("date-checkout") as HTMLInputElement;
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    dateCheckin.valueAsDate = today;
    dateCheckout.valueAsDate = tomorrow;
  }

  checkinInput(event: Event) {
    const dateCheckin = event.target as HTMLInputElement;
    const dateCheckout = document.getElementById("date-checkout") as HTMLInputElement;
    const checkinDate = new Date(dateCheckin.value);
    const checkoutDate = new Date(dateCheckout.value);

    if (checkinDate > checkoutDate) {
      const newCheckoutDate = new Date(checkinDate);
      newCheckoutDate.setDate(newCheckoutDate.getDate() + 1);
      dateCheckout.valueAsDate = newCheckoutDate;
    }
  }

  checkoutInput(event: Event) {
    const dateCheckin = document.getElementById("date-checkin") as HTMLInputElement;
    const dateCheckout = event.target as HTMLInputElement;
    const checkinDate = new Date(dateCheckin.value);
    const checkoutDate = new Date(dateCheckout.value);

    if (checkoutDate < checkinDate) {
      const newCheckinDate = new Date(checkoutDate);
      newCheckinDate.setDate(newCheckinDate.getDate() - 1);
      dateCheckin.valueAsDate = newCheckinDate;
    }
  }

  toggleReservationBoxSize() {
    const reservationBox = document.querySelector('.reservation-box') as HTMLElement;
    const button = document.querySelector('.test') as HTMLElement;
    reservationBox.classList.toggle('small');
    button.classList.toggle('small');
  }

}
<p>table-reservation works!</p>
<div class="reservation-box">
  <div class="top">
    <div class="static">
      <div class="input-container" id="date-picker-container">
        <label for="date-from">check-in</label>
        <input type="date" id="date-checkin" class="date-field" name="date-from" (input)="checkinInput($event)">
      </div>
    </div>
    <div class="flex">
      <div class="input-container" id="date-picker-container">
        <label for="date-from">check-out</label>
        <input type="date" id="date-checkout" class="date-field" name="" (input)="checkoutInput($event)">
      </div>
      <div class="button-container">
        <span class="button ok">availability</span>
      </div>
    </div>
  </div>
  <div class="bottom">
    <!-- <a href="#" class="info" title="" target="new">Click here for a full overview.</a> -->
  </div>
</div>

<div class="test" title="Toggle reservation box size small/normal" (click)="toggleReservationBoxSize()">
  <svg xmlns="http://www.w3.org/2000/svg" width="36" height="16" viewBox="0 0 36 14">
    <rect id="switch" data-name="switch" width="36" height="14" rx="7"/>
  </svg>
</div>
