import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  formData: any = {
    name: '',
    mob: ''
  };
  constructor(private loginService: AuthService,) {}
  onSubmit() {
    // You can send the formData to a service here
    console.log(this.formData);
    // Example: Call a service to send the data
    this.loginService.register(this.formData).subscribe(response => {
      console.log(response);
    });
  }
}
<p>register works!</p>
<!DOCTYPE html>
<html lang="en">
<head>
	<title>unsubscribe Form</title>
	<meta charset="UTF-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
    <!-- <link rel="stylesheet" href="./asset/css/register.css"> -->
</head>
<body>
    <div class="limiter">
        <div class="container-login100">
            <div class="wrap-login100 p-l-55 p-r-55 p-t-65 p-b-50">
                <form class="login100-form" method="POST">
                    <span class="login100-form-title p-b-33">
                        Register
                    </span>

                    <div class="wrap-input100">
                        <label for="id">Name:</label>
                        <input class="input100" type="text" id="name" name="name" placeholder="Enter Name" autocomplete="off">
                        <span class="focus-input100-1"></span>
                        <span class= "focus-input100-2"></span>
                    </div><br><br>
                    <div class="wrap-input100">
                        <label for="id">Mobile:</label>
                        <input class="input100" type="text" id="mob" name="mob" placeholder="Enter Mobile Number" autocomplete="off" oninput="validateInput(this);" maxlength="10">
                        <span class="focus-input100-1"></span>
                        <span class="focus-input100-2"></span>
                    </div>
                    <div id="error-message" style="color: red;"></div>
                    <!-- Add an element to display the response message -->
                    <div id="response-message" style="color: green;"></div>

                    <div class="container-login100-form-btn m-t-20">
                        <button class="login100-form-btn">Register</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
</body>
<!-- <script src="./asset/script/customScript.js"></script> -->
</html>
