import { Component } from '@angular/core';

@Component({
  selector: 'app-table-reservation',
  templateUrl: './table-reservation.component.html',
  styleUrls: ['./table-reservation.component.css']
})
export class TableReservationComponent {
  checkinDate: Date = new Date(); ;
  checkoutDate: Date = new Date(); ;


  onchange() {
    // Get the current date and time
    const currentDate = new Date(); // Replace with the actual way to get the current date and time from your library.

    // Get the check-in and check-out dates and times from the input fields
    const checkinDate = new Date(this.checkinDate); // Replace with your actual input field's value.
    const checkoutDate = new Date(this.checkoutDate); // Replace with your actual input field's value.

    // Check if checkin date is less than current date
    if (checkinDate < currentDate) {
      alert('Check-in date is in the past. Please select a future date.');
      return;
    }

    // Check if checkin date is the same as the current date but checkin time is less than current time
    if (
      checkinDate.toDateString() === currentDate.toDateString() &&
      checkinDate.getTime() < currentDate.getTime()
    ) {
      alert('Check-in time is in the past. Please select a future time for check-in.');
      return;
    }

    // Check if checkout date is less than checkin date
    if (checkoutDate < checkinDate) {
      alert('Check-out date cannot be before the check-in date. Please select a valid check-out date.');
      return;
    }

    // Check if checkout date is the same as checkin date but checkout time is before checkin time
    if (
      checkoutDate.toDateString() === checkinDate.toDateString() &&
      checkoutDate.getTime() < checkinDate.getTime()
    ) {
      alert('Check-out time cannot be before the check-in time. Please select a valid check-out time.');
      return;
    }

    // Continue with your other logic or actions if all conditions pass.
  }

  // ...


  // toggleReservationBoxSize() {
  //   const reservationBox = document.querySelector('.reservation-box') as HTMLElement;
  //   const button = document.querySelector('.test') as HTMLElement;
  //   reservationBox.classList.toggle('small');
  //   button.classList.toggle('small');
  // }

}
<p>table-reservation works!</p>
<div class="reservation-box">
  <div class="top">
    <div class="static">
      <div class="input-container" id="date-picker-container">
        <label for="date-from">check-in</label>
        <input type="datetime-local" id="date-checkin" class="date-field" name="date-from" (change)="onchange()" >

      </div>
    </div>
    <div class="flex">
      <div class="input-container" id="date-picker-container">
        <label for="date-from">check-out</label>
        <input type="datetime-local" id="date-checkout" class="date-field" name="" >
    </div>
      <div class="button-container">
        <span class="button ok">availability</span>
      </div>
    </div>
  </div>
  <div class="bottom">
    <!-- <a href="#" class="info" title="" target="new">Click here for a full overview.</a> -->
  </div>
</div>

